package com.repoJava.danielC;

public class OperadoresRelacionales {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*
		 * Operadores Relacionales
		 * 
		 * 	Operador	 Nombre 	Ejemplo
		 * 		<		Menor que		a<b
		 * 		>		Mayor que		a>b
		 * 		<=		Menor o igual   a<=b
		 * 					que
		 * 		>=		Mayor o igual	a>=b
		 * 					que	
		 * 
		 */
		
		int a =5;
		int b =7;
		int c =5;
		
		System.out.println(" Numeros a comparar a = 5 b = 7 c=5");
		System.out.println("Operador relacional mayor que b>a: "+ (b > a) );
		System.out.println("Operador relacional menor que a<b: " +(a<b));
		System.out.println("Operador relacional Menor o igual que a>=c: "+(a>=c) );
		System.out.println("Operador relacional Mayor o igual que b<=c: "+(b<=c));
		
		
		
	}

}
